/*randomly generated probabilities*/

prob_both:float((rand(2)+1)/10)
prob_A: float(prob_both + (rand(4)+1)/10)
prob_B: float(prob_both + (rand(4)+1)/10)
prob_AgivenB: float(prob_both/prob_B)


/*possible answers*/
ta1:decimalplaces((prob_AgivenB*prob_B)/prob_A,2);
ta2_AnB:ev((prob_AgivenB)*(prob_B));
ta2:decimalplaces(1-(prob_A+prob_B-(ta2_AnB)),2);
ta3:decimalplaces(prob_B-ta2_AnB,2);

stackprt.feedbackvariables:
/*Node1: checking correct answer*/

/*Node2: Assuming probability of B given A is same as probability of A given B*/
mistake1:prob_both/prob_B;

/*Node3: Finding only the probability of B intersection A*/
mistake2:prob_both;

/*Node1:checking coreect answer*/

/*Node2:Using 1- P(B ^C∩ A) + P(B ∩ A^C)*/
mistake1:1- prob_A-prob_both + prob_B-prob_both +prob_both;

/*Node1:checking correct answer*/

/*Node2:Using p(A) - P(B ∩ A)*/
mistake1:prob_A-prob_both;

stackoptions.questionvariables:
/*Randomly generate and calculate the initial probabilities*/
red_beads: rand([3,4,5,6,7]); /*the number of red beads*/
blue_beads:rand([2,3,4]); /* the number of blue beads */
green_beads:rand([3,4,5]); /* the number of green beads*/
selected_beads:3;/* the number of beads selected from the bag*/
total_beads:red_beads+blue_beads+green_beads ; /*the total number of beads*/

/*model answers*/
ta1:((red_beads/total_beads)*((red_beads-1)/(total_beads-1))*(blue_beads/(total_beads-2))) + ((red_beads/total_beads)*((red_beads-1)/(total_beads-1))*(green_beads/(total_beads-2)));

ta2: (selected_beads)! *(red_beads/total_beads)*(blue_beads/(total_beads-1)*(green_beads/(total_beads-2)));

stackprt.feedbackvariables:
/*Node 1: check for correct answer*/

/*Node 2: mistake: adding P(2*red *blue) + P(2*red *green) */
mistake_2:((red_beads/total_beads)*((red_beads)/(total_beads))*(blue_beads/(total_beads))) + ((red_beads/total_beads)*((red_beads)/(total_beads))*(green_beads/(total_beads)))

/*Node 1: check for correct answer*/

/*Node 2: mistake: not multiplying the P(one red, one blue and one green) by 3*/
mistake_2:(red_beads/total_beads) *(blue_beads/(total_beads-1)*(green_beads/(total_beads-2)));
/*Node 3: mistake*/
ta4:decimalplaces(float((selected_beads !) *  (red_beads/total_beads) *(blue_beads/(total_beads-1)*(green_beads/(total_beads-2)))),2);

