aa:(rand([5,6,7,8])/10);/*f(x)=x0-variable*/
bb:(rand([1,2])/10);/*f(x)=x2-variable*/
func:(aa-variable)+variable+(bb+variable); /*PMF*/
ans:solve(func=1)[1];  

/*Model answer*/
ta:rhs(ans);
ta1:aa-ta;
ta2:ta;
ta3:bb+ta;

stackprt.feedbackvariables:
/*node 1 checking for correct answer*/
/*node 2:mistake:Subtracting the {@variable @} from f(x2)*/
mistake_2:(aa-variable)+variable+(bb-variable); /*PMF*/
sans2:rhs(solve(mistake_2=1)[1]);
/*node 3:mistake:Adding the {@variable@} from f(x0)*/
mistake_3:(aa+variable)+variable+(bb+variable); /*PMF*/
sans3:rhs(solve(mistake_3=1)[1]);

/*node 1 checking for correct answer*/
/*node 2:mistake:Finding a negative probability for f(x=0)*/
mistake_2:aa-ta;
