stackoptions.questionvariables:
/*randomisation of the weights*/
kk: rand([9,4,14]);
w1_l: rand([40,30,50]); /*The left of the weight 1*/
w1_r: w1_l+kk; /*The right of the weight 1*/
w2_l: w1_r+1; /*The left of the weight 2*/
w2_r: w2_l+kk; /*The right of the weight 2*/
w3_l: w2_r+1; /*The right of the weight 3*/
w3_r: w3_l+kk; /*The right of the weight 3*/
