freq_2: rand([10,20,24,8]); /*Frequence*/
freq_3: rand([17,5,11,13]); /*Frequence*/
freq_4: rand([3,5,7,12]); /*Frequence*/

/*Computation of the cumulative frequency*/
cum_freq1: freq_1;
cum_freq2: freq_1+freq_2;
cum_freq3: cum_freq2+freq_3;
cum_freq4: cum_freq3+freq_4;

/*Index of percentile to compute*/ 
index_1: rand_with_step(10,35,5);
index_2: rand_with_step(46,70,4);




/*Computation*/
p_index1: (index_1*cum_freq4)/100;
p_index2: (index_2*cum_freq4)/100;

/*Identification of classes*/
class_1: 1;
class_2: 2;
class_3: 3;
class_4: 4;
class_5: 5;

class_wrt_index1: if p_index1 > cum_freq4 then class_5 elseif p_index1 > cum_freq3 then class_4 elseif p_index1 > cum_freq2 then class_3 elseif p_index1 > cum_freq1 then class_2 else class_1;

class_wrt_index2: if p_index2 > cum_freq4 then class_5 elseif p_index2 > cum_freq3 then class_4 elseif p_index2 > cum_freq2 then class_3 elseif p_index2 > cum_freq1 then class_2 else class_1;

/*Pick up correct boundaries*/
bd_class1: w1_l-1/2;
bd_class2: w1_r+1/2;
bd_class3: w2_r+1/2;
bd_class4: w3_r+1/2;
bd_class5: w4_r+1/2;

bd_wrt_index1: if class_wrt_index1=class_4 then bd_class4 elseif class_wrt_index1=class_3 then bd_class3 elseif class_wrt_index1=class_2 then bd_class2 elseif class_wrt_index1=class_1 then bd_class1 elseif class_wrt_index1=class_5 then bd_class5;

bd_wrt_index2: if class_wrt_index2=class_4 then bd_class4 elseif class_wrt_index2=class_3 then bd_class3 elseif class_wrt_index2=class_2 then bd_class2 elseif class_wrt_index2=class_1 then bd_class1 elseif class_wrt_index2=class_5 then bd_class5;


choix_cumfreq_class: if class_wrt_index1=class_2 then cum_freq1 elseif class_wrt_index1=class_3 then cum_freq2 elseif class_wrt_index1=class_4 then cum_freq3 elseif class_wrt_index1=class_5 then cum_freq4 else cum_freq1;/*Choose cumulative frequency depending of class in numerator*/

choix_cumfreq_class_s: if class_wrt_index2=class_2 then cum_freq1 elseif class_wrt_index2=class_3 then cum_freq2 elseif class_wrt_index2=class_4 then cum_freq3 elseif class_wrt_index2=class_5 then cum_freq4 else cum_freq1;/*Choose cumulative frequency depending of class in numerator*/  


choix_freq_den: if class_wrt_index1=class_2 then freq_2 elseif class_wrt_index1=class_3 then freq_3 elseif class_wrt_index1=class_4 then freq_4 elseif class_wrt_index1=class_5 then freq_4 else freq_1; /*Choix of frequency wrt to denominator and its class*/

choix_freq_den_s: if class_wrt_index2=class_2 then freq_2 elseif class_wrt_index2=class_3 then freq_3 elseif class_wrt_index2=class_4 then freq_4  elseif class_wrt_index2=class_5 then freq_4 else freq_1; /*Choix of frequency wrt to denominator and its class*/

/*Compute final percentiles*/
percentile_1: bd_wrt_index1+((p_index1-choix_cumfreq_class)/choix_freq_den)*w_th;
percentile_2: bd_wrt_index2+((p_index2-choix_cumfreq_class_s)/choix_freq_den_s)*w_th;

/*defining power of the category of classes*/
exponent1_class: if class_wrt_index1=class_1 then "st" elseif class_wrt_index1=class_2 then "nd" elseif class_wrt_index1=class_3 then "rd"  elseif class_wrt_index1=class_4 then "th" else "th";
exponent2_class: if class_wrt_index2=class_1 then "st" elseif class_wrt_index2=class_2 then "nd" elseif class_wrt_index2=class_3 then "rd"  elseif class_wrt_index2=class_4 then "th" else "th";

/*Worked solution*/
sol1: if integerp(percentile_1)=true then round(percentile_1) else decimalplaces(percentile_1,1);
sol2: if integerp(percentile_2)=true then round(percentile_2) else decimalplaces(percentile_2,1);

