/*Node 1: correct answer*/
/*Node 2: Express in 1 decimal places*/
/*Node 3: Interchanging cumulative frequence and frequence*/
mistake_1: bd_wrt_index1+((p_index1-choix_freq_den)/choix_cumfreq_class)*w_th;

/*Node 1: correct answer*/
/*Node 2: Express in 1 decimal places*/
/*Node 3: Interchanging cumulative frequence and frequence*/
mistake_1: bd_wrt_index2+((p_index2-choix_freq_den_s)/choix_cumfreq_class_s)*w_th;

stackoptions.questionvariables:
/*Randomize the numbers 0.2, 0.3 and 0.1*/

prob_both:(rand(2)+1)/10; /*p(hitting the target with both shots) =0.1*/
prob_first:(rand(4)+1)/10 + prob_both; /*p(hitting the target with the first shot) = 0.2*/
prob_second:(rand_with_step(1,4,1))/10+ prob_both; /*p(hitting with the second shot) = 0.3*/

/*model answer*/

total:(prob_first-prob_both)+(prob_second-prob_both)+prob_both;
prob_comb:1-total;
ta1:prob_comb; /*p(Missing the target with both shots)*/

prob_miss:prob_first-prob_both;
ta2:prob_miss; /*p(hitting first shot and missing with the second shot)*/

stackprt.feedbackvariables:
/*Node1:checking the correct answer*/

/*Node2:checking the answer given as 1- p(hitting the target with both shots) to find the p(missing the target with both shots)*/
mistake_2:1- prob_both;

/*Node1:checking the correct answer*/

/*Node2: checking the answer given as p(Hitting both shots) instead of hitting shot 1 and missing shot 2*/
mistake_2:prob_both;

/*Node3:checking the answer given as p(hitting the target with first shot)-P(hitting the target with both shots) to find p(hitting with the first shot and missing with the second)*/
